import React, { useState } from "react";
import { View, TextInput, Button } from "react-native";
import { useDispatch } from "react-redux";
import addPosts from "../features/post/postSlice"
import { nanoid } from '@reduxjs/toolkit'


const CreatePost = () => {
    
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");

    const dispatch = useDispatch();

    const onTitleChanged = setTitle(e.target.value)
    const onContentChanged = setTitle(e.target.value)

    const onSaveClicked = dispatch(addPosts({
      id: nanoid(),
      title
    }))
  return (
    <View>
      <TextInput placeholder="Title" onChangeText={onTitleChanged} value={title}/>
      <TextInput placeholder="Title" onChangeText={onContentChanged} value={content}/>
      <Button title="Save Post" />
    </View>
  );
};

export default CreatePost;
