import { createSlice } from "@reduxjs/toolkit";

const initialState = [
    {id:'123', title:"World", content:"Hello World"},
    {id:'124', title:"Space", content:"Hello Space"},
    {id:'125', title:"BlackMatter", content:"Hello BlackMatter"},
    {id:'126', title:"Space", content:"Hello Space"}
]

const postsSlice = createSlice({
    name:"post",
    initialState,
    reducers:{
        addPosts(state, action) {
            state.push(action.payload)
        }
    }
})
export const {addPosts} = postsSlice.actions
export default postsSlice.reducer
