import React, { useState } from "react";
import { View, TextInput, Button } from "react-native";
import { useDispatch } from "react-redux";
import addPosts from "../features/post/postSlice"
import { nanoid } from '@reduxjs/toolkit'


const CreatePost = () => {
    
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");

    const dispatch = useDispatch();

    const onTitleChanged = e => setTitle(e)
    const onContentChanged = e => setContent(e)

    const onSaveClicked = () => {
    if(title && content) {
      dispatch(
        addPosts({
        id: nanoid(),
        title,
        content
      }))
      setTitle('')
      setContent('')
    } 
    }
      
  return (
    <View>
      <TextInput placeholder="Title" onChangeText={onTitleChanged} value={title}/>
      <TextInput placeholder="Title" onChangeText={onContentChanged} value={content}/>
      <Button title="Save Post" on={onSaveClicked} />
    </View>
  );
};

export default CreatePost;
