import React from "react";
import {StyleSheet, View, Text, Image} from "react-native"
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import HomeScreen from "../screens/HomeScreen";
import ChatScreen from "../screens/ChatScreen";
import PostScreen from "../screens/PostScreen";
import SettingsScreen from "../screens/SettingsScreen";
import FindScreen from "../screens/FindScreen";

const Tab = createBottomTabNavigator();

const Tabs = () => {
  return (
    <Tab.Navigator
    tabBarOptions={{
      showLabel:false,
      style:{
        position:"absolute",
        bottom: 25,
        left:20,
        right:20,
        elevation:0,
        backgroundColor:"black",
        borderRadius:15,
        height:90,
        ...styles.shadow

      }
    }}>
      <Tab.Screen name="Home" component={HomeScreen} options={{
        tabBarIcon:({focused}) => (
          <View style={{alignItems:"center", justifyContent:"center", top:10}}>
            <Image
             source={require("../assets/images/logo.png")}
             resizeMode="contain"
             style={{
               width: 25,
               height: 25,
               tintColor:focused ? "#e32f45" : "#74c94",
             }} />
            <Text style={{color:focused ? "#e32f45" : "#74c94",fontSize:12}}>HOME</Text>
          </View>
        )
      }} />
      <Tab.Screen name="Find" component={FindScreen} 
      
      options={{
        tabBarIcon:({focused}) => (
          <View style={{alignItems:"center", justifyContent:"center", top:10}}>
            <Image
             source={require("../assets/images/logo.png")}
             resizeMode="contain"
             style={{
               width: 25,
               height: 25,
               tintColor:focused ? "#e32f45" : "#74c94",
             }} />
            <Text style={{color:focused ? "#e32f45" : "#74c94",fontSize:12}}>FIND</Text>
          </View>
        )
      }}/>
      <Tab.Screen name="Post" component={PostScreen} 
      options={{
        tabBarIcon:({focused}) => (
          <View style={{alignItems:"center", justifyContent:"center", top:10}}>
            <Image
             source={require("../assets/images/logo.png")}
             resizeMode="contain"
             style={{
               width: 25,
               height: 25,
               tintColor:focused ? "#e32f45" : "#74c94",
             }} />
            <Text style={{color:focused ? "#e32f45" : "#74c94",fontSize:12}}>PPOST</Text>
          </View>
        )
      }}/>
      <Tab.Screen name="Settings" component={SettingsScreen} />
      <Tab.Screen name="Chat" component={ChatScreen} />
    </Tab.Navigator>
  );
}

const styles = StyleSheet.create({
  shadow:{
    shadowColor:"#7F5Df0",
    shadowOffset:{
      width:0,
      height:10
    },
    shadowOpacity:0.25,
    shadowRadius:3.5,
    elevation:5
  }
})

export default Tabs;
