import { createSlice } from "@reduxjs/toolkit";

const initialState = [
    {id:'123', title:"World", content:"Hello World"},
    {id:'124', title:"Space", content:"Hello Space"},
    {id:'125', title:"BlackMatter", content:"Hello BlackMatter"},
    {id:'126', title:"Alien", content:"Hello Alien"}
]

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {
      postAdded: {
        reducer(state, action) {
          state.push(action.payload)
        },
        prepare(title, content, userId) {
          return {
            payload: {
              id: nanoid(),
              title,
              content,
              user: userId,
              date: new Date().toISOString(),
            }
          }
        }
      },
    }
    
})

export const {postAdded} = postsSlice.actions
export default postsSlice.reducer
